mapper.insert:
insert into district (DISTRICT_ID, CITY_CODE, DISTRICT_CODE, 
  DISTRICT_NAME, SHORT_NAME, LNG, 
  LAT, SORT, GMT_CREATE, 
  GMT_MODIFIED, MEMO, DATA_STATE
  )
values (#{districtId,jdbcType=INTEGER}, #{cityCode,jdbcType=VARCHAR}, #{districtCode,jdbcType=VARCHAR}, 
  #{districtName,jdbcType=VARCHAR}, #{shortName,jdbcType=VARCHAR}, #{lng,jdbcType=VARCHAR}, 
  #{lat,jdbcType=VARCHAR}, #{sort,jdbcType=INTEGER}, #{gmtCreate,jdbcType=TIMESTAMP}, 
  #{gmtModified,jdbcType=TIMESTAMP}, #{memo,jdbcType=VARCHAR}, #{dataState,jdbcType=INTEGER}
  )

mapper.select:
select

from district
where 1=1

  AND DISTRICT_ID = #{map.districtId,jdbcType=INTEGER}


  AND CITY_CODE = #{map.cityCode,jdbcType=VARCHAR}


  AND DISTRICT_CODE = #{map.districtCode,jdbcType=VARCHAR}


  AND DISTRICT_NAME = #{map.districtName,jdbcType=VARCHAR}


  AND SHORT_NAME = #{map.shortName,jdbcType=VARCHAR}


  AND LNG = #{map.lng,jdbcType=VARCHAR}


  AND LAT = #{map.lat,jdbcType=VARCHAR}


  AND SORT = #{map.sort,jdbcType=INTEGER}


  AND GMT_CREATE = #{map.gmtCreate,jdbcType=TIMESTAMP}


  AND GMT_MODIFIED = #{map.gmtModified,jdbcType=TIMESTAMP}


  AND MEMO = #{map.memo,jdbcType=VARCHAR}


  AND DATA_STATE = #{map.dataState,jdbcType=INTEGER}
