mapper.insert:
insert into provincial_agent (provincial_agent_id, provincial_agent_name, 
  member_id, member_name, provincial_agent_status, 
  provincial_agent_created_date, provincial_agent_updated_date
  )
values (#{provincialAgentId,jdbcType=VARCHAR}, #{provincialAgentName,jdbcType=VARCHAR}, 
  #{memberId,jdbcType=VARCHAR}, #{memberName,jdbcType=VARCHAR}, #{provincialAgentStatus,jdbcType=INTEGER}, 
  #{provincialAgentCreatedDate,jdbcType=TIMESTAMP}, #{provincialAgentUpdatedDate,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT

FROM
    provincial_agent
WHERE
    1 = 1

  AND provincial_agent_name = #{provincialAgentName,jdbcType=VARCHAR}


  AND member_id = #{memberId,jdbcType=VARCHAR}


  AND member_name = #{memberName,jdbcType=VARCHAR}


  AND provincial_agent_status = #{provincialAgentStatus,jdbcType=INTEGER}


  AND provincial_agent_created_date = #{provincialAgentCreatedDate,jdbcType=TIMESTAMP}


  AND provincial_agent_updated_date = #{provincialAgentUpdatedDate,jdbcType=TIMESTAMP}
