mapper.select:
SELECT

,

FROM
    provincial_agent_item
WHERE
    1 = 1

  AND provincial_agent_id = #{map.provincialAgentId,jdbcType=VARCHAR}


  AND provincial_agent_name = #{map.provincialAgentName,jdbcType=VARCHAR}


  AND item_id = #{map.itemId,jdbcType=VARCHAR}


  AND item_name = #{map.itemName,jdbcType=VARCHAR}


  AND classify_id = #{map.classifyId,jdbcType=VARCHAR}


  AND classify_name = #{map.classifyName,jdbcType=VARCHAR}


  AND provincial_agent_item_original_price = #{map.provincialAgentItemOriginalPrice,jdbcType=DOUBLE}


  AND provincial_agent_item_present_price = #{map.provincialAgentItemPresentPrice,jdbcType=DOUBLE}


  AND provincial_agent_item_url = #{map.provincialAgentItemUrl,jdbcType=VARCHAR}


  AND stock_num = #{map.stockNum,jdbcType=INTEGER}


  AND sales_num = #{map.salesNum,jdbcType=INTEGER}


  AND provincial_agent_item_status = #{map.provincialAgentItemStatus,jdbcType=INTEGER}


  AND provincial_agent_item_type = #{map.provincialAgentItemType,jdbcType=INTEGER}


  AND provincial_agent_item_created_date = #{map.provincialAgentItemCreatedDate,jdbcType=TIMESTAMP}


  AND provincial_agent_item_updated_date = #{map.provincialAgentItemUpdatedDate,jdbcType=TIMESTAMP}


  AND provincial_agent_item_desc = #{map.provincialAgentItemDesc,jdbcType=LONGVARCHAR}
